    "import requests\n",
    "import os\n",
    "\n",
    "def detect_earthquake():\n",
    "    url = \"https://opendata.cwa.gov.tw/api/v1/rest/datastore/E-A0015-001\"\n",
    "    headers = {\n",
    "        \"Authorization\": \"CWA-738C42B9-49B2-412C-BD7C-080975813398\"\n",
    "    }\n",
    "    response = requests.get(url, headers=headers)\n",
    "    data = response.json()\n",
    "\n",
    "    # 印出返回的資料，檢查資料結構\n",
    "    print(data)\n",
    "\n",
    "    # 檢查 'records' 和 'location' 是否存在\n",
    "    if \"records\" in data and \"location\" in data[\"records\"]:\n",
    "        if data[\"records\"][\"location\"]:\n",
    "            webhook_url = os.environ.get(\"WEBHOOK_URL\")\n",
    "            requests.post(webhook_url, json=data)\n",
    "    else:\n",
    "        print(\"No earthquake data found.\")\n",
    "        \n",
    "# 呼叫函數來偵測地震\n",
    "detect_earthquake()"
